index.css:
.lesson-content .err-lineno {
    display: inline-block;
    background-color: #FCC
    }
.lesson-content .err-exctype {
    display: inline-block;
    background-color: #CFC
    }

.lesson-content .green {
    color: #0a0
    }
.lesson-content .red {
    color: #a00
    }
.lesson-content .yellow {
    color: #a50
    }
.lesson-content .strong {
    font-weight: bold
    }
.lesson-content .blue {
    color: #0aa
    }

.lesson-content .part-green {
    outline: 2px solid hsla(113, 100%, 50%, 1);
    background-color: hsla(113, 100%, 50%, 0.25)
    }
.lesson-content .part-cyan {
    outline: 2px solid hsla(180, 100%, 50%, 1);
    background-color: hsla(180, 100%, 50%, 0.25)
    }
.lesson-content .part-blue {
    outline: 2px solid hsla(236, 100%, 50%, 1);
    background-color: hsla(236, 100%, 50%, 0.25)
    }
.lesson-content .part-yellow {
    outline: 2px solid hsla(60, 100%, 50%, 1);
    background-color: hsla(60, 100%, 50%, 0.25)
    }
.lesson-content .part-orange {
    outline: 2px solid hsla(42, 100%, 50%, 1);
    background-color: hsla(42, 100%, 50%, 0.25)
    }
.lesson-content .part-red {
    outline: 2px solid hsla(0, 100%, 50%, 1);
    background-color: hsla(0, 100%, 50%, 0.25)
    }

.lesson-content .highlight {
    background-color: hsla(0, 100%, 50%, 0.1)
    }
.lesson-content .highlight-nocolor {
    background-color: hsla(60, 100%, 50%, 0.75)
    }
.lesson-content .highlight-red {
    background-color: hsla(0, 100%, 50%, 0.25)
    }
.lesson-content .highlight-green {
    background-color: hsla(113, 100%, 50%, 0.25)
    }
.lesson-content .highlight-blue {
    background-color: hsla(236, 100%, 50%, 0.25)
    }
